import { Check, CheckBoxOutlineBlankTwoTone } from "@mui/icons-material";
import {
  Button,
  ButtonGroup,
  Container,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Stack,
  Typography,
} from "@mui/material";
import React, { useState } from "react";

const FilterTodo = () => {
  // Your challenge is to add a filter to do the following
  // Show completed todos
  // Show incomplete todos
  // Show all todos
  // The todos data structure is as follows

  const todos = [
    {
      id: 1,
      title: "NeoG assignments",
      completed: false,
    },
    {
      id: 2,
      title: "Pair programming session",
      completed: true,
    },
    {
      id: 3,
      title: "Project submission",
      completed: false,
    },
    {
      id: 4,
      title: "Coding practice",
      completed: true,
    },
  ];
  const [isCompleted, setIsCompleted] = useState(todos);
  const checkIfCompleted = (arr) => {
    if (todos.isCompleted === false)
      return arr.filter((todo) => !todo.completed);
    if (todos.isCompleted) return arr.filter((todo) => todo.completed);
    return arr;
  };
  return (
    <Container>
      <Stack direction={"column"} spacing={2} maxWidth="35rem">
        <Typography variant="h3">Filter Todos</Typography>
        <ButtonGroup variant="contained">
          <Button onClick={setIsCompleted({ ...isCompleted, allTodos: true })}>
            Show All Todos
          </Button>
          <Button
            onClick={setIsCompleted({ ...isCompleted, isCompleted: true })}
          >
            Show Completed Todos
          </Button>
          <Button
            onClick={setIsCompleted({ ...isCompleted, isCompleted: false })}
          >
            Show Incomplete Todos
          </Button>
        </ButtonGroup>
        <Stack>
          {isCompleted.map((todo) => {
            return (
              <List>
                <ListItem>
                  <ListItemIcon>
                    <Check />
                  </ListItemIcon>
                  <ListItemText primary={todo.title} />
                </ListItem>
              </List>
            );
          })}
        </Stack>
      </Stack>
    </Container>
  );
};

export default FilterTodo;
