import { Check, CheckBoxOutlineBlankTwoTone } from "@mui/icons-material";
import {
  Button,
  ButtonGroup,
  Container,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Stack,
  Typography,
} from "@mui/material";
import React, { useState } from "react";

const FilterTodo = () => {
  // Your challenge is to add a filter to do the following
  // Show completed todos
  // Show incomplete todos
  // Show all todos
  // The todos data structure is as follows

  const todos = [
    {
      id: 1,
      title: "NeoG assignments",
      completed: false,
    },
    {
      id: 2,
      title: "Pair programming session",
      completed: true,
    },
    {
      id: 3,
      title: "Project submission",
      completed: false,
    },
    {
      id: 4,
      title: "Coding practice",
      completed: true,
    },
  ];
  const [toggleTodos, setToggleTodos] = useState({
    allTodos: true,
    isCompleted: true,
  });
  return (
    <Container>
      <Stack direction={"column"} spacing={2} maxWidth="35rem">
        <Typography variant="h3">Filter Todos</Typography>
        <ButtonGroup variant="contained">
          <Button
            onClick={setToggleTodos((prev) => ({ ...prev, allTodos: true }))}
          >
            Show All Todos
          </Button>
          <Button
            onClick={setToggleTodos((prev) => ({
              ...prev,
              allTodos: false,
              isCompleted: true,
            }))}
          >
            Show Completed Todos
          </Button>
          <Button
            onClick={setToggleTodos((prev) => ({
              ...prev,
              allTodos: false,
              isCompleted: false,
            }))}
          >
            Show Incomplete Todos
          </Button>
        </ButtonGroup>
        <Stack>
          {todos
            .filter((todo) => {
              if (toggleTodos.allTodos) return todo;
              if (toggleTodos.isCompleted) return todo.completed;
              return !todo.completed;
            })
            .map((todo) => {
              return (
                <List>
                  <ListItem>
                    <ListItemIcon>
                      <Check />
                    </ListItemIcon>
                    <ListItemText primary={todo.title} />
                  </ListItem>
                </List>
              );
            })}
        </Stack>
      </Stack>
    </Container>
  );
};

export default FilterTodo;
